    */
    private int CheckName()
    {
        foreach (Player player in Game.players)
        {
            if (player.name == "")
            {

                return 1;
            }
            else if (Game.players.FindAll(p => p.name == player.name).Count > 1)
            {

                return 2;
            }
        }
        return 0;
    }

